// Profile.js
import './Profile.css'
import { useEffect, useState } from 'react';
import axios from 'axios';
import './ChatGroupCards.css'
import CardItem from './CardItem';
import './Cards.css';
function Profile() {
  const [user, setUser] = useState({});
  const token = sessionStorage.getItem("token");

  useEffect(() => {
    const getProfileData = async () => {
      try {
        const response = await axios.get("http://localhost:3001/app/me", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        setUser(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    getProfileData();
  }, [token]);

  return (
    <>
      <div className='container'>
      <div className='profile__info'>
          <p className='profile__name'>Name: {user.UserName}</p>
          <p className='profile__email'>Email: {user.UserEmail}</p>
          <p>Chat Groups:</p>
          <ul className='cards__items'>
            {user.ChatGroups &&
              user.ChatGroups.map((chatGroup) => (
                <CardItem
                  key={chatGroup._id}
                  src='images/img-2.jpg'
                  text={`Create your ${chatGroup.chatGroupName} based chat group features like voice chatting and high performance`}
                  label='Chat Group'
                  path='/services'
                  chatGroupName={chatGroup.chatGroupName}
                />
              ))}
          </ul>
        </div>
      </div>
    </>
  );
}

export default Profile;
