// Profile.js

import { useEffect, useState } from 'react';
import axios from 'axios';
import './ChatGroupCards.css'
import CardItem from './CardItem';
import './Cards.css';
function Profile() {
  const [user, setUser] = useState({});
  const token = sessionStorage.getItem("token");

  useEffect(() => {
    const getProfileData = async () => {
      try {
        const response = await axios.get("http://localhost:3001/app/me", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        setUser(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    getProfileData();
  }, [token]);

  return (
    <>
      <div className='container'>
        <div>
          <p>Name: {user.UserName}</p>
          <p>Email: {user.UserEmail}</p>
          <p>Chat Groups:</p>
          <ul className='cards__items'>
            <CardItem
              src='images/img-2.jpg'
              text='Create your game based chat group features like voice chatting and high performance'
              label='Game'
              path='/services'
              chatGroupName='Game Lovers'
            />
          {user.ChatGroups &&
              user.ChatGroups.map((chatGroupdetails) => (
                <li key={chatGroupdetails._id}><CardItem
                src='images/img-2.jpg'
                text='Create your game based chat group features like voice chatting and high performance'
                label='Game'
                path='/services'
                chatGroupName='Game Lovers'
              />{chatGroupdetails.chatGroupName}</li>
            ))}
          </ul>
        </div>
      </div>
    </>
  );
}

export default Profile;
