// Profile.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import "./ChatGroupCards.css";
import CardItem from "./ChatGroupCardItem";
import "./Cards.css";
import FriendsList from "./ProfileRelated/FriendsList";

function Profile() {
  const [user, setUser] = useState({});
  const [friends, setFriends] = useState([]);
  const token = sessionStorage.getItem("token");

  useEffect(() => {
    const getProfileData = async () => {
      try {
        const response = await axios.get("http://localhost:3001/app/me", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        setUser(response.data);
        setFriends(response.data.Friends); // "Friends" alanını kullanıcı verisinden alalım
      } catch (error) {
        console.error(error);
      }
    };
    getProfileData();
  }, [token]);

  return (
    <>
      <div className="container">
        <div className="profile__info">
          <div className="profile__pic-wrap">
            <img className="profile__pic profile__pic--default" src="https://www.anagard.com/blog/wp-content/uploads/2014/02/2011-03-12-twitter-egg.png" alt="Profile" />
          </div>
          <div className="profile__details">
            <p className="profile__name">Name: {user.UserName}</p>
            <p className="profile__email">Email: {user.UserEmail}</p>
          </div>
        </div>
        <p>Chat Groups:</p>
        <div className="cards__container">
          <div className="cards__wrapper">
            <ul className="cards__items">
              {user.ChatGroups &&
                user.ChatGroups.map((chatGroup) => (
                  <CardItem
                    key={chatGroup._id}
                    src="images/img-2.jpg"
                    label="Chat Group"
                    path="/chat"
                    chatGroupName={chatGroup.chatGroupName}
                  />
                ))}
            </ul>
          </div>
        </div>
        <FriendsList friends={friends} />
      </div>
    </>
  );
}

export default Profile;
