import React, { useEffect, useState } from "react";
import axios from "axios";
import "../ChatGroupRelated/ChatGroupPage.css"
import { useParams } from "react-router-dom";
import FriendsList from "../ProfileRelated/FriendsList";

export default function Chats( ) {
    const [ friends, setFriends ] = useState([]);
    const [ chatGroup, setChatGroup ] = useState({});
    constÂ { chatGroupId } = useParams();
    const token = sessionStorage.getItem("token");

  useEffect(() => {
    console.log(chatGroupId);
    const getChatGroupData = async () => {
      try {
        const chatGroupData = await axios.get(`http://localhost:3001/app/get-chat-group/${chatGroupId}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setChatGroup(chatGroupData.data);
      } catch (error) {
        console.log(error);
      }
    };
    getChatGroupData();
  }, [chatGroupId, token]);

  useEffect(() => {
    console.log(chatGroupId);
    const addFriendToChatGroup = async () => {
      try {
        const chatGroupData = await axios.get(`http://localhost:3001/app/get-chat-group/${chatGroupId}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setChatGroup(chatGroupData.data);
      } catch (error) {
        console.log(error);
      }
    };
  }, [chatGroupId, token]);

  useEffect(() => {
    const getFriendsofUser = async () => {
      try {
        const response = await axios.get("http://localhost:3001/app/me", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setFriends(response.data.Friends); 
      } catch (error) {
        console.error(error);
      }
    };
    getFriendsofUser();
  }, [token]);

  return (
    <div className="chat-group__container"> 
      <h5 className="chat-group__name">{chatGroup.chatGroupName}</h5>
      <h6 className="chat-group__email">Chat Group ID: {chatGroup._id}</h6>
      <h6>Group Members:</h6>
      <ul>
        {chatGroup.users &&
          chatGroup.users.map((user) => (
            <li key={user._id}>{user._id}</li>
          ))}
      </ul>
        <FriendsList friends={friends}/>
    </div>

  );
}
