import React, { useState } from 'react';
import axios from 'axios';
import './SignUp.css';
import { Button } from './Button';


const CreateChatGroup = () => {
  const [chatGroupName, setChatGroupName] = useState('');
  const [loading, setLoading] = useState(false);

  const handleCreateChatGroup = async () => {
    try {
      setLoading(true);
      const response = await axios.post(
        'http://localhost:3001/chat-groups/create-chat-group',
        { chatGroupName },
        { withCredentials: true } 
      );
      console.log(response.data);
      // Grup oluşturulduktan sonra sayfayı yenileyebilir veya başka bir işlem yapabilirsiniz.
    } catch (error) {
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className='sign-up'>
        <div className='sign-up__container'>



        </div>
    </div>
  );
};

export default CreateChatGroup;
