import React from "react";
import axios from "axios";

function FriendCard({ friend }) {
  const token = sessionStorage.getItem("token");
  const handleAddFriend = async () => {
    try {
      // Arkadaş olarak eklemek için API çağrısı yap
      await axios.post(
        `http://localhost:3001/app/add-friend/${friend._id}`,
        {},
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      alert(`${friend.name} is now your friend!`);
    } catch (error) {
      console.error(error);
    }
  };
  const handleRemoveFriend = async () => {
    try {
      // Arkadaş olarak eklemek için API çağrısı yap
      await axios.post(
        `http://localhost:3001/app/remove-friend/${friend._id}`,
        {},
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      alert(`${friend.name} is remooved from friends list!`);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="friend-card">
      <img src={friend.avatarUrl} alt={friend.name} />
      <h2>{friend.name}</h2>
      <button onClick={handleAddFriend}>Add Friend</button>
      <div className="friend-card">
      <img src={friend.avatarUrl} alt={friend.name} />
      <h2>{friend.name}</h2>
      <button onClick={handleAddFriend}>Add Friend</button>
    </div>
    </div>
  );
}

export default FriendCard;
