import React, { useState, useEffect } from "react";
import axios from "axios";

function FriendCard({ friend, currentUserFriendEmails }) {
  const token = sessionStorage.getItem("token");
  const [isFriend, setIsFriend] = useState(false);

  useEffect(() => {
    // Arkadaş listesindeki kullanıcının email adresini kontrol et
    setIsFriend(currentUserFriendEmails.includes(friend.email));
  }, [currentUserFriendEmails, friend.email]);

  const handleFriendAction = async (action) => {
    try {
      // Arkadaş olarak eklemek veya çıkarmak için API çağrısı yap
      const endpoint = action === "add" ? "add-friend" : "remove-friend";
      const response = await axios.post(
        `http://localhost:3001/app/${endpoint}/${friend.email}`,
        {},
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      // Yanıtı işleyerek duruma göre uyarı ver
      if (response.status === 200) {
        if (action === "add") {
          alert(`${friend.name} is now your friend!`);
          setIsFriend(true);
        } else {
          alert(`${friend.name} is removed from your friends list!`);
          setIsFriend(false);
        }
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="friend-card">
      <img src={friend.avatarUrl} alt={friend.name} />
      <h2>{friend.name}</h2>
      {isFriend ? (
        <button onClick={() => handleFriendAction("remove")}>Remove Friend</button>
      ) : (
        <button onClick={() => handleFriendAction("add")}>Add Friend</button>
      )}
    </div>
  );
}

export default FriendCard;
