import React, { useState } from 'react';
import axios from 'axios';

const CreateChatGroup = () => {
  const [chatGroupName, setChatGroupName] = useState('');
  const [loading, setLoading] = useState(false);

  const handleCreateChatGroup = async () => {
    try {
      setLoading(true);
      const response = await axios.post(
        'http://localhost:3001/chat-groups/create-chat-group',
        { chatGroupName }
      );
      console.log(response.data);
      // Grup oluşturulduktan sonra sayfayı yenileyebilir veya başka bir işlem yapabilirsiniz.
    } catch (error) {
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Create a New Chat Group</h2>
      <div>
        <label>Chat Group Name:</label>
        <input
          type="text"
          value={chatGroupName}
          onChange={(e) => setChatGroupName(e.target.value)}
        />
      </div>
      <button onClick={handleCreateChatGroup} disabled={loading}>
        Create Group
      </button>
    </div>
  );
};

export default CreateChatGroup;
