import React from 'react';
import { Link } from 'react-router-dom';

function CardItem(props) {
  const { src, label, path, chatGroupName, friendToAdd, setFriendToAdd } = props;
  const handleAddFriendToGroup = () => {
    setFriendToAdd(chatGroupName); // Set the friendToAdd state when the button is clicked
  };
  return (
    <>
      <li className='cards__item'>
        <Link className='cards__item__link' to={path}>
          <figure className='cards__item__pic-wrap' data-category={label}>
            <img className='cards__item__img' alt='Travel Image' src={src} />
          </figure>
          <div className='cards__item__info'>
            <h5 className='cards__item__text'>{props.text}</h5>
            {friendToAdd === chatGroupName && (
              <button onClick={handleAddFriendToGroup}>Add Friend to Chat Group</button>
            )}
          </div>
        </Link>
      </li>
    </>
  );
}

export default CardItem;
