import React, { useState, useEffect } from "react";
import axios from "axios";
import FriendCard from "../FriendCard";

function Network() {
  const token = sessionStorage.getItem("token");
  const [searchText, setSearchText] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [currentUserFriends, setCurrentUserFriends] = useState([]);

  useEffect(() => {
    const fetchFriends = async () => {
      try {
        const response = await axios.get("http://localhost:3001/app/get-friends", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setCurrentUserFriends(response.data.map((friend) => friend._id));
      } catch (error) {
        console.error(error);
      }
    };
    fetchFriends();
  }, [token]);

  const handleSearch = async () => {
    try {
      // Kullanıcı adına veya e-posta adresine göre arama yap
      const response = await axios.get(
        `http://localhost:3001/app/search-user?searchText=${searchText}`
      );
      setSearchResults(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleFriendAdded = (friendId) => {
    // Yeni arkadaş eklendiğinde "currentUserFriends" durumunu güncelle
    setCurrentUserFriends((prevFriends) => [...prevFriends, friendId]);
  };

  const handleFriendRemoved = (friendId) => {
    // Arkadaş çıkarıldığında "currentUserFriends" durumunu güncelle
    setCurrentUserFriends((prevFriends) => prevFriends.filter((id) => id !== friendId));
  };

  return (
    <div className="network">
      <input
        type="text"
        value={searchText}
        onChange={(e) => setSearchText(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>
      <div className="friend-cards">
        {searchResults.map((friend) => (
          <FriendCard
            key={friend._id}
            friend={friend}
            currentUserFriends={currentUserFriends}
            onFriendAdded={handleFriendAdded}
            onFriendRemoved={handleFriendRemoved}
          />
        ))}
      </div>
    </div>
  );
}

export default Network;
